mapper.select:
SELECT id,name,logo,is_delete,
   created_by,created_time,updated_time
FROM `brand`


        and id=#{id,jdbcType=NUMERIC}


        and name=#{name,jdbcType=VARCHAR}


        and logo=#{logo,jdbcType=VARCHAR}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


        and created_by=#{createdBy,jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.name,t.logo,t.is_delete,
t.created_by,t.created_time,t.updated_time
FROM `brand` t 


    AND name=#{name,jdbcType=VARCHAR}


    AND logo=#{logo,jdbcType=VARCHAR}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND created_by=#{createdBy,jdbcType=NUMERIC}


    AND (
         name LIKE CONCAT('%', #{keywords}, '%')
         OR logo LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}
