mapper.select:
SELECT id,name,description,limit_cost,
   amount,is_delete,created_time,updated_time
FROM `coupons`


        AND id=#{id,jdbcType=NUMERIC}


        AND name=#{name,jdbcType=VARCHAR}


        AND description=#{description,jdbcType=VARCHAR}


        AND limit_cost=#{limitCost,jdbcType=NUMERIC}


        AND amount=#{amount,jdbcType=NUMERIC}


        AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.name,t.description,t.limit_cost,
t.amount,t.is_delete,t.created_time,t.updated_time
