SELECT id,name,element_type,parent_id,
   url,is_delete,created_time,updated_time
FROM `element`


        and id=#{id,jdbcType=NUMERIC}


        and name=#{name,jdbcType=VARCHAR}


        and element_type=#{elementType, javaType=com.yc.trip.api.business.enums.element.ElementType, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


        and parent_id=#{parentId,jdbcType=NUMERIC}


        and url=#{url,jdbcType=VARCHAR}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.name,t.element_type,t.parent_id,
t.url,t.is_delete,t.created_time,t.updated_time
FROM `element` t 


    AND name=#{name,jdbcType=VARCHAR}


    AND element_type=#{elementType, javaType=com.yc.trip.api.business.enums.element.ElementType, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND parent_id=#{parentId,jdbcType=NUMERIC}


    AND url=#{url,jdbcType=VARCHAR}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         name LIKE CONCAT('%', #{keywords}, '%')
         OR url LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}
