SELECT id,provider_id,store_id,motorcade_type,
   link_man,link_num,is_delete,created_time,
   updated_time
FROM `motorcade`


        AND id=#{id,jdbcType=NUMERIC}


        AND provider_id=#{providerId,jdbcType=NUMERIC}


        AND store_id=#{storeId,jdbcType=NUMERIC}


        AND motorcade_type=#{motorcadeType, javaType=com.yc.trip.api.business.enums.motorcade.MotorcadeType, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


        AND link_man=#{linkMan,jdbcType=VARCHAR}


        AND link_num=#{linkNum,jdbcType=VARCHAR}


        AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

