mapper.select:
SELECT id,order_id,product_id,user_id,
   credit,comment,is_delete,created_time,
   updated_time
FROM `product_comment`


        and id=#{id,jdbcType=NUMERIC}


        and order_id=#{orderId,jdbcType=NUMERIC}


        and product_id=#{productId,jdbcType=NUMERIC}


        and user_id=#{userId,jdbcType=NUMERIC}


        and credit=#{credit,jdbcType=NUMERIC}


        and comment=#{comment,jdbcType=VARCHAR}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.order_id,t.product_id,t.user_id,
t.credit,t.comment,t.is_delete,t.created_time,
t.updated_time
FROM `product_comment` t 


    AND order_id=#{orderId,jdbcType=NUMERIC}


    AND product_id=#{productId,jdbcType=NUMERIC}


    AND user_id=#{userId,jdbcType=NUMERIC}


    AND credit=#{credit,jdbcType=NUMERIC}


    AND comment=#{comment,jdbcType=VARCHAR}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         comment LIKE CONCAT('%', #{keywords}, '%')
     )


