SELECT id,provider_name,link_man,link_num,
   address,total_days,end_time,sub_count,
   is_delete,created_time,updated_time
FROM `provider`


        AND id=#{id,jdbcType=NUMERIC}


        AND provider_name=#{providerName,jdbcType=VARCHAR}


        AND link_man=#{linkMan,jdbcType=VARCHAR}


        AND link_num=#{linkNum,jdbcType=VARCHAR}


        AND address=#{address,jdbcType=VARCHAR}


        AND total_days=#{totalDays,jdbcType=NUMERIC}


        AND sub_count=#{subCount,jdbcType=NUMERIC}


        AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

