mapper.select:
SELECT id,provider_name,link_man,link_num,
   address,end_time,sub_count,
   is_delete,created_time,updated_time
FROM `provider`


        and id=#{id,jdbcType=NUMERIC}


        and provider_name=#{providerName,jdbcType=VARCHAR}


        and link_man=#{linkMan,jdbcType=VARCHAR}


        and link_num=#{linkNum,jdbcType=VARCHAR}


        and address=#{address,jdbcType=VARCHAR}


        and sub_count=#{subCount,jdbcType=NUMERIC}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.provider_name,t.link_man,t.link_num,
t.address,t.end_time,t.sub_count,
t.is_delete,t.created_time,t.updated_time, t.sub_count as remain_sub_count, t.sub_count as used_sub_count
FROM `provider` t 


    AND provider_name=#{providerName,jdbcType=VARCHAR}


    AND link_man=#{linkMan,jdbcType=VARCHAR}


    AND link_num=#{linkNum,jdbcType=VARCHAR}


    AND address=#{address,jdbcType=VARCHAR}


    AND sub_count=#{subCount,jdbcType=NUMERIC}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         provider_name LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}



    AND EXISTS (select 1 from provider_brand_relation r where r.brand_id = #{brandId} and r.provider_id = t.id)


    AND sub_count=#{remainSubCount}


    AND t.created_time >= #{queryStartTime}


     AND t.created_time <= #{queryEndTime"}
