mapper.select:
SELECT id,parent_id,region_sort_id,name,
   is_hot,is_delete,created_time,updated_time
FROM `region`


        and id=#{id,jdbcType=NUMERIC}


        and parent_id=#{parentId,jdbcType=NUMERIC}


        and region_sort_id=#{regionSortId,jdbcType=NUMERIC}


        and name=#{name,jdbcType=VARCHAR}


        and is_hot=#{isHot, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.parent_id,t.region_sort_id,t.name,
t.is_hot,t.is_delete,t.created_time,t.updated_time
FROM `region` t 


    AND parent_id=#{parentId,jdbcType=NUMERIC}


    AND region_sort_id=#{regionSortId,jdbcType=NUMERIC}


    AND name=#{name,jdbcType=VARCHAR}


    AND is_hot=#{isHot, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         name LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}
