mapper.select:
SELECT id,name,is_delete,created_time,
   updated_time
FROM `region_sort`


        and id=#{id,jdbcType=NUMERIC}


        and name=#{name,jdbcType=VARCHAR}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.name,t.is_delete,t.created_time,
t.updated_time
FROM `region_sort` t 


    AND name=#{name,jdbcType=VARCHAR}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         name LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}
