   credit,credit_allot_status,pre_allot_time,ack_allot_time,
   is_delete,created_time,updated_time
FROM `sales_credit`


        AND id=#{id,jdbcType=NUMERIC}


        AND user_id=#{userId,jdbcType=NUMERIC}


        AND allot_id=#{allotId,jdbcType=NUMERIC}


        AND order_id=#{orderId,jdbcType=NUMERIC}


        AND credit=#{credit,jdbcType=NUMERIC}


        AND credit_allot_status=#{creditAllotStatus, javaType=com.yc.trip.api.business.enums.sales.CreditAllotStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


        AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

