SELECT id,title,store_id,image_url,
   start_time,end_time,is_delete,created_time,
   updated_time
FROM `special_offer`


        AND id=#{id,jdbcType=NUMERIC}


        AND title=#{title,jdbcType=VARCHAR}


        AND store_id=#{storeId,jdbcType=NUMERIC}


        AND image_url=#{imageUrl,jdbcType=VARCHAR}


        AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.title,t.store_id,t.image_url,
t.start_time,t.end_time,t.is_delete,t.created_time,
t.updated_time
FROM `special_offer` t 


    AND title=#{title,jdbcType=VARCHAR}


    AND store_id=#{storeId,jdbcType=NUMERIC}


    AND image_url=#{imageUrl,jdbcType=VARCHAR}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         title LIKE CONCAT('%', #{keywords}, '%')
         OR image_url LIKE CONCAT('%', #{keywords}, '%')
     )


    AND id IN

            #{item}



AND date_format(curdate(), '%Y-%m-%d') between date_format(t.start_time, '%Y-%m-%d') and date_format(t.end_time, '%Y-%m-%d')
