mapper.select:
SELECT id,user_id,content,location,
   longitude,latitude,is_delete,created_time,
   updated_time
FROM `trip_share`


        and id=#{id,jdbcType=NUMERIC}


        and user_id=#{userId,jdbcType=NUMERIC}


        and content=#{content,jdbcType=VARCHAR}


        and location=#{location,jdbcType=VARCHAR}


        and longitude=#{longitude,jdbcType=NUMERIC}


        and latitude=#{latitude,jdbcType=NUMERIC}


        and is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


LIMIT 2

SELECT t.id,t.user_id,t.content,t.location,
t.longitude,t.latitude,t.is_delete,t.created_time,
t.updated_time
FROM `trip_share` t 


    AND user_id=#{userId,jdbcType=NUMERIC}


    AND content=#{content,jdbcType=VARCHAR}


    AND location=#{location,jdbcType=VARCHAR}


    AND longitude=#{longitude,jdbcType=NUMERIC}


    AND latitude=#{latitude,jdbcType=NUMERIC}


    AND is_delete=#{isDelete, javaType=com.yc.trip.api.core.enums.YesNoStatus, typeHandler=org.go.api.core.typehandler.UniversalEnumHandler, jdbcType=NUMERIC}


    AND (
         location LIKE CONCAT('%', #{keywords}, '%')
     )


